Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('13','caratula','CÓDIGO CLAUSULADO SU-OD-08-04');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('1','caratula','ESTA POLIZA SE EXPIDE EN CONSIDERACION A LAS DECLARACIONES HECHAS POR EL TOMADOR EN CUALQUIER DOCUMENTO APORTADO PARA LA EXPEDICION, LOS CUALES FORMAN PARTE INTEGRANTE DE ESTA POLIZA.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('2','caratula','LA MORA EN EL PAGO DE LA PRIMA DE LA PÓLIZA O DE LOS CERTIFICADOS O ANEXOS QUE SE EXPIDAN CON FUNDAMENTO EN ELLA, PRODUCIRÁ LA TERMINACIÓN AUTOMÁTICA DEL CONTRATO Y DARÁ DERECHO AL ASEGURADOR PARA EXIGIR EL PAGO DE LA PRIMA DEVENGADA Y DE LOS GASTOS CAUSADOS CON OCASIÓN DE LA EXPEDICIÓN DEL CONTRATO DE SEGURO.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('3','caratula','LAS PARTES ACUERDAN QUE EL PAGO SE DEBERA HACERA MAS TARDAR DENTRO DEL MES SIGUIENTE CONTADO A PARTIR DE LA FECHA DE LA INICIACION DE LA VIGENCIA DE LA POLIZA O, SI FUERE EL CASO, DE LOS CERTIFICADOS O ANEXOS QUE SE EXPIDAN CON FUNDAMENTO EN ELLA.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('4','caratula','LAS CARÁTULAS ENVIADAS VÍA MAIL TIENEN FIRMA AUTORIZADA Y UN CERTIFICADO DIGITAL, CUALQUIER ALTERACIÓN A SU CONTENIDO ACARREARÁ LAS SANCIONES ESTIPULADAS EN LA LEY.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('5','caratula','CON LA FIRMA Y/O EL RECIBO A SATISFACCIÓN DE LA PRESENTE PÓLIZA, DECLARO DE MANERA EXPRESA EN MI CALIDAD DE TOMADOR, QUE CONOCÍ DE MANERA ANTICIPADA EL CLAUSULADO Y ME FUERON EXPLICADOS SUFICIENTEMENTE LOS EFECTOS Y ALCANCE DE LAS CONDICIONES GENERALES, DE LA COBERTURA, DE LAS EXCLUSIONES Y DE LAS GARANTÍAS DE LA MISMA. TAMBIÉN ME INFORMARON QUE PUEDO CONSULTAR TODOS LOS CONDICIONADOS DE MANERA PREVIA Y PERMANENTEMENTE EN LA PÁGINA WEB WWW. CONFIANZA.COM.CO. Y RECIBIR EXPLICACIONES DE LOS MISMOS CON MI INTERMEDIARIO DE SEGUROS ASÍ COMO EN CUALQUIERA DE LAS OFICINAS Y SUCURSALES DE CONFIANZA. S.A EN TODO EL PAÍS.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('6','caratula','***VER NOTA*** EN LOS CASOS EN QUE EL VALOR ASEGURADO SEA EXPRESADO EN MONEDA EXTRANJERA, CON FUNDAMENTO EN LO DISPUESTO EN EL ARTÍCULO 2.31.2.1.1 DEL DECRETO 2555 DE 2010, LOS PAGOS DE LA PRIMA SÓLO SE PODRÁN REALIZAR MEDIANTE TRANSFERENCIA ELECTRÓNICA O CHEQUE, SUJETANDOSE INTEGRALMENTE A LAS DISPOSICIONES VIGENTES EN MATERIA CAMBIARIA, SEGÚN LO SEÑALADO POR EL ÁRTICULO 2.31.2.1.4 DEL MISMO ORDENAMIENTO, EL VALOR DE LA PRIMA NETA SE HARA EFECTIVO A LA TASA REPRESENTATIVA DEL MERCADO EN EL DIA DE SU PAGO, EL VALOR DEL IVA Y LOS GASTOS SE LIQUIDARÁN A LA TASA REPRESENTATIVA DEL MERCADO VIGENTE EN LA FECHA DE EXPEDICION.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('7','caratula','SOMOS GRANDES CONTRIBUYENTES-I.V.A.-REGIMEN COMUN-AGENTES DE RETENCIÓN, RESPONSABLES DE INDUSTRIA Y COMERCIO EN BOGOTA, D.C');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('8','caratula','SI ESTA PÓLIZA SE CONSTITUYE MEDIANTE UN INTERMEDIARIO, LA PRIMA DE ESTA PÓLIZA INCLUYE EL VALOR DE LA COMISIÓN QUE CORRESPONDE AL INTERMEDIARIO DE SEGUROS. EL INTERMEDIARIO DE SEGUROS PUEDE RECIBIR REMUNERACIÓN ADICIONAL DEL ASEGURADOR BASADO EN ESTA PÓLIZA. DICHA REMUNERACIÓN ES CONFORME CON LAS LEYES Y REGULACIONES APLICABLES.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('9','caratula','LA PRESENTACION DE ESTA POLIZA ANTE EL ASEGURADO, EN MI CALIDAD DE TOMADOR IMPLICA MI ACEPTACION Y AUTORIZACION PARA QUE EN EL EVENTO EN QUE INCUMPLA LAS OBLIGACIONES QUE EMANAN DEL PRESENTE CONTRATO DE SEGURO, MI NOMBRE Y LAS CIRCUNSTANCIAS Y CARACTERISTICAS DE MI INCUMPLIMIENTO, SEAN REPORTADAS A LAS CENTRALES DE DATOS EXISTENTES EN EL PAIS. ');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('10','caratula','LA PRESENTE GARANTIA NO EXPIRARA POR FALTA DE PAGO DE LA PRIMA DE LA POLIZA O DE LOS CERTIFICADOS O ANEXOS QUE SE EXPIDA CON FUNDAMENTO EN ELLA O POR REVOCACION UNILATERAL POR PARTE DEL TOMADOR O DE LA COMPAÑIA, DE CONFORMIDAD CON LO PREVISTO EN EL NUMERAL 19 DEL ARTICULO 25 DE LA LEY 80 DE 1993.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('11','caratula','AUTORIZACIÓN DATOS PERSONALES: DE MANERA EXPRESA MANIFIESTO Y ACEPTO QUE EN MI CALIDAD DE: I) TOMADOR Y/O, II) ASEGURADO Y/O BENEFICIARIO, QUE EL HECHO DE TOMAR, ACEPTAR Y/O RECIBIR LA PRESENTE PÓLIZA EN LAS CALIDADES ANTES MENCIONADAS, CONSTITUYE UNA CONDUCTA INEQUÍVOCA DE LA CUAL SE CONCLUYE MI AUTORIZACIÓN EXPRESA PARA EL TRATAMIENTO DE MIS DATOS PERSONALES POR PARTE DE CONFIANZA S.A, INCLUIDOS LOS SENSIBLES, ASÍ COMO MI AUTORIZACIÓN PARA LA TRANSFERENCIA Y TRANSMISIÓN NACIONAL E INTERNACIONAL DE MIS DATOS, POR LAS PERSONAS, PARA LAS FINALIDADES Y EN LOS TÉRMINOS QUE ME FUERON INFORMADOS EN EL AVISO DE PRIVACIDAD PUBLICADO EN WWW.CONFIANZA.COM.CO Y CONFORME CON EL MANUAL DE PROTECCIÓN DE DATOS PERSONALES PUBLICADO EN EL MISMO PORTAL.');
Insert into TEXTO_JSPR (ID,CONCEPTO,TEXTO) values ('12','CLAUSULA','EXCLUSION DE TRANSACCIONES PROHIBIDAS, EMBARGOS Y SANCIONES ECONÓMICAS:  
