--SE ELIMINAN LOS FORMULARIOS DEL USUARIO Y REGISTROS EXISTENTES QUE SE INSERTARÁN POSTERIORMENTE
UPDATE CFG_USER SET CCFGFORM = 'CFG_CENTRAL' WHERE CUSER = 'AXIS_RIESGOS';
DELETE FROM CFG_FORM WHERE CCFGFORM LIKE 'CFG_RIESGOS';
--DELETE FROM CFG_COD_FORM WHERE CFORM IN ('');
DELETE FROM CFG_FORM_PROPERTY WHERE CIDCFG = 80;
DELETE FROM CFG_COD_CFGFORM_DET WHERE CCFGFORM = 'CFG_RIESGOS'; 
DELETE FROM CFG_COD_CFGFORM WHERE CCFGFORM = 'CFG_RIESGOS'; 


